from Crypto.Util.number import *
from secret import FLAG

def prng(x):
    a = 13105568607311865900719288866098594505025142450950723803234150868508669378850618674764530617802138416698278735457536272217755011333702651331642969928238378
    b = 7609894268236428025470144180108229816262546135610797250928076003741685805220771552782762560301070130423255021856512797670433007975921480065990823381229326
    m = getRandomNBitInteger(512)
    while True:
        x = (a * x + b) % m
        yield x % m


p = prng(bytes_to_long(FLAG))

k = [next(p) for i in range(10)]
print(k)
